enum MPCError {
    MaliciousDealer,
    InvalidBitsize,
    InvalidAggregation,
    InvalidGeneratorsLength,
    WrongNumBitCommitments,
    WrongNumPolyCommitments,
    WrongNumProofShares,
    MalformedProofShares {
            bad_shares: Vec<usize>,
        },
}

enum R1CSError {
    InvalidGeneratorsLength,
    FormatError,
    VerificationError,
    MissingAssignment,
    GadgetError {
            description: String,
        },
}

fn new<'a,
'b>(&'b BulletproofGens, &'b PedersenGens, &'a mut Transcript, usize, usize)
-> Result<DealerAwaitingBitCommitments, MPCError>

fn assign_position_with_rng<T>(Self, usize, &mut T)
-> Result<(PartyAwaitingBitChallenge, BitCommitment), MPCError> where
T: RngCore + CryptoRng

fn prove_multiple(&BulletproofGens, &PedersenGens, &mut Transcript, &[u64],
&[Scalar], usize)
-> Result<(RangeProof, Vec<CompressedRistretto>), ProofError>

fn verify_single_with_rng<T>(&Self, &BulletproofGens, &PedersenGens,
&mut Transcript, &CompressedRistretto, usize, &mut T)
-> Result<(), ProofError> where T: RngCore + CryptoRng

fn verify_multiple_with_rng<T>(&Self, &BulletproofGens, &PedersenGens,
&mut Transcript, &[CompressedRistretto], usize, &mut T)
-> Result<(), ProofError> where T: RngCore + CryptoRng
